sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
var rightNow=new GlideDateTime('incident');
	var when_needed=new GlideDateTime(current.u_when_needed);
	if(when_needed.before(rightNow)){
		gs.addErrorMessage("When needeDate Cannot be in the Past");
		current.setAbortAction(true);
		}
	var today=rightNow.getLocalDate();
	var istoday=when_needed.getLocalDate();
	if(istoday.comapreTo(today)==0){
		gs.addErrorMessage('You cannot enter the same date at when needed');
		current.setAbortAction(true);
	}
})(current, previous);
