sys_script.script:
(function executeRule(current, previous /*null when async*/) {
//check for before Date
	var rightnow=new GlideDateTime();
	var whenNeeded=new GlideDateTime(current.u_when_needed);
	if(whenNeeded.before(rightnow)){
		gs.addErrorMessage("When Need It date cannot be in the past.Your request  not been safe to database");
	current.setAbortAction(true);
	}
	// Add your code here

	//check for Today
	var today=rightnow.getLocalDate();
	var whenN=whenNeeded.getLocalDate();
	if(whenN.compareTo(today)==0){
		gs.addErrorMessage("Cannot Enter today's Date");
		current.setAbortAction(true);
	}


})(current, previous);
