sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var whatNeeded=g_form.getValue('u_choice_5');
	g_form.clearOptions('u_choice_5');//clear the list


  if(newValue=="Human Resource"){
	  g_form.addOption('u_choice_5','hr1','Human Resource');
	  g_form.addOption('u_choice_5','hr2','Human Resource');
	  g_form.addOption('u_choice_5','other','Other');
  }
	if(newValue=="facilities"){
		g_form.addOption('u_choice_5','facilities1','Facilities 1');
		g_form.addOption('u_choice_5','facilities2','Facilities 2');
		g_form.addOption('u_choice_5','other','Other');
	}
	if(newValue=="legal"){
		g_form.addOption('u_choice_5','Legal1','Legal 1');
		g_form.addOption('u_choice_5','Legal2','Legal 2');
		g_form.addOption('u_choice_5','other','Other');
	}
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_choice_5',whatNeeded);
	}

}
